mapper.delete:
UPDATE auth_customer SET is_del = 1
WHERE
    1=1


        and id =  #{id}



        and account =  #{account}



        and password =  #{password}



        and employee_code =  #{employeeCode}



        and type =  #{type}



        and lock_flag =  #{lockFlag}



        and is_del =  #{isDel}



        and code =  #{code}



        and rfid_container_code =  #{rfidContainerCode}



        and name =  #{name}



        and rfid_code =  #{rfidCode}



        and auth_position_code =  #{authPositionCode}



        and auth_department_code =  #{authDepartmentCode}



        and auth_agency_code =  #{authAgencyCode}

mapper.select:
SELECT
/*authCustomer begin*/

    auth_customer.id as 'auth_customer.id',

