FROM auth_customer as auth_customer
LEFT JOIN auth_agency as auth_agency ON auth_agency.id = auth_customer.auth_agency_code
LEFT JOIN auth_department as auth_department ON auth_department.id = auth_customer.auth_department_code
LEFT JOIN auth_position as auth_position ON auth_position.id = auth_customer.auth_position_code
LEFT JOIN rfid_container as rfid_container ON rfid_container.code = auth_customer.rfid_container_code
WHERE
    1 = 1 AND  auth_customer.is_del = 0
/*auth_customer begin*/


        and auth_customer.id = #{id}



        and auth_customer.account = #{account}



        and auth_customer.password = #{password}



        and auth_customer.employee_code = #{employeeCode}



        and auth_customer.type = #{type}



        and auth_customer.lock_flag = #{lockFlag}



        and auth_customer.is_del = #{isDel}



        and auth_customer.code = #{code}



