        and auth_customer.rfid_code = #{rfidCode}



        and auth_customer.auth_position_code = #{authPositionCode}



        and auth_customer.auth_department_code = #{authDepartmentCode}



        and auth_customer.auth_agency_code = #{authAgencyCode}

/*auth_customer end*/
