FROM cutting_tool_bindle_records as cutting_tool_bindle_records
LEFT JOIN product_line_assemblyline as product_line_assemblyline ON product_line_assemblyline.code = cutting_tool_bindle_records.product_line_assemblyline_code
LEFT JOIN product_line_axle as product_line_axle ON product_line_axle.code = cutting_tool_bindle_records.product_line_axle_code
LEFT JOIN product_line_equipment as product_line_equipment ON product_line_equipment.code = cutting_tool_bindle_records.product_line_equipment_code
LEFT JOIN product_line_parts as product_line_parts ON product_line_parts.code = cutting_tool_bindle_records.product_line_parts_code
LEFT JOIN product_line_process as product_line_process ON product_line_process.code = cutting_tool_bindle_records.product_line_process_code
LEFT JOIN synthesis_cutting_tool as synthesis_cutting_tool ON synthesis_cutting_tool.code = cutting_tool_bindle_records.synthesis_cutting_tool_code
WHERE
1 = 1 AND  cutting_tool_bindle_records.is_del = 0
/*cutting_tool_bindle_records begin*/


    and cutting_tool_bindle_records.id = #{id}



    and cutting_tool_bindle_records.product_line_equipment_code = #{productLineEquipmentCode}



