    and cutting_tool_bindle_records.product_line_equipment_name = #{productLineEquipmentName}



    and cutting_tool_bindle_records.product_line_parts_name = #{productLinePartsName}



    and cutting_tool_bindle_records.product_line_axle_name = #{productLineAxleName}



    and cutting_tool_bindle_records.product_line_process_name = #{productLineProcessName}



    and cutting_tool_bindle_records.rated_number = #{ratedNumber}



    and cutting_tool_bindle_records.actual_number = #{actualNumber}



    and cutting_tool_bindle_records.is_del = #{isDel}



    and cutting_tool_bindle_records.operator_name = #{operatorName}



    and cutting_tool_bindle_records.operator = #{operator}



    and cutting_tool_bindle_records.operator_time <= #{operatorTimeEnd}


    and cutting_tool_bindle_records.operator_time >= #{operatorTimeBegin}

