mapper.insert:
INSERT INTO DJ_LINE_AKP (

    LINE_CODE,

    LINE_NAME
)
VALUES (

    #{lineCode},

    #{lineName}
)

mapper.delete:
DELETE FROM DJ_LINE_AKP
WHERE
    1=1


        and LINE_CODE =  #{lineCode}



        and LINE_NAME =  #{lineName}

mapper.update:
UPDATE DJ_LINE_AKP SET
LINE_NAME = #{lineName},
LINE_CODE = #{lineCode} WHERE 1 = 1 and LINE_CODE = #{lineCode}

mapper.select:
SELECT
/*djLineAkp begin*/

    DJ_LINE_AKP.LINE_CODE as 'DJ_LINE_AKP.LINE_CODE',

    DJ_LINE_AKP.LINE_NAME as 'DJ_LINE_AKP.LINE_NAME'
/*djLineAkp end*/
FROM DJ_LINE_AKP as DJ_LINE_AKP
WHERE
    1 = 1
/*DJ_LINE_AKP begin*/


        and DJ_LINE_AKP.LINE_CODE = #{lineCode}



        and DJ_LINE_AKP.LINE_NAME = #{lineName}

/*DJ_LINE_AKP end*/
ORDER BY DJ_LINE_AKP.LINE_CODE desc

LIMIT #{startRecord},#{pageSize}

SELECT COUNT(1)
FROM DJ_LINE_AKP as DJ_LINE_AKP
WHERE
    1 = 1
/*DJ_LINE_AKP begin*/


    and DJ_LINE_AKP.LINE_CODE = #{lineCode}



    and DJ_LINE_AKP.LINE_NAME = #{lineName}

/*DJ_LINE_AKP end*/

SELECT
/*DJ_LINE_AKP begin*/

DJ_LINE_AKP.LINE_CODE as 'DJ_LINE_AKP.LINE_CODE',

DJ_LINE_AKP.LINE_NAME as 'DJ_LINE_AKP.LINE_NAME'
/*DJ_LINE_AKP end*/
FROM DJ_LINE_AKP as DJ_LINE_AKP
WHERE
    1 = 1
/*DJ_LINE_AKP begin*/


    and DJ_LINE_AKP.LINE_CODE = #{lineCode}



    and DJ_LINE_AKP.LINE_NAME = #{lineName}

/*DJ_LINE_AKP end*/
