mapper.insert:
INSERT INTO DJ_USER_AKP (
    /* 用户ID */
    USER_ID,
    /* 用户名 */
    USER_NAME,
    /* 密码 */
    PASSWORD,
    /* 姓名 */
    NAME,
    /* 性别 */
    TITLE
)
VALUES (
    /* 用户ID */
    #{userId},
    /* 用户名 */
    #{userName},
    /* 密码 */
    #{password},
    /* 姓名 */
    #{name},
    /* 性别 */
    #{title}
)

mapper.delete:
DELETE FROM DJ_USER_AKP
WHERE
1=1
/* 用户ID */

    and USER_ID =  #{userId}

/* 用户名 */

    and USER_NAME =  #{userName}

/* 密码 */

    and PASSWORD =  #{password}

/* 姓名 */

    and NAME =  #{name}

/* 性别 */

    and TITLE =  #{title}

mapper.update:
UPDATE DJ_USER_AKP
SET
/* 用户名 */

    USER_NAME =  #{userName},

/* 密码 */

    PASSWORD =  #{password},

/* 姓名 */

    NAME =  #{name},

/* 性别 */

    TITLE =  #{title},

/* 用户ID */
USER_ID = #{userId}
WHERE
1 = 1
and USER_ID = #{userId}

