mapper.delete:
UPDATE material_inventory SET is_del = 1
WHERE
    1=1


        and id =  #{id}



        and warehouse_code =  #{warehouseCode}



        and prepare_library_count =  #{prepareLibraryCount}



        and for_grinding_in_count =  #{forGrindingInCount}



        and for_grinding_out_count =  #{forGrindingOutCount}



        and grinding_out_count =  #{grindingOutCount}



        and product_line_count =  #{productLineCount}



        and scrap_count =  #{scrapCount}



        and cutting_tool_code =  #{cuttingToolCode}



        and is_del =  #{isDel}



        and to_install_count =  #{toInstallCount}



        and to_uninstall_count =  #{toUninstallCount}



        and to_exchange_count =  #{toExchangeCount}

