mapper.delete:
UPDATE cutting_tools_scrap SET is_del = 1
WHERE
    1=1


        and id =  #{id}



        and tool_type =  #{toolType}



        and material_num =  #{materialNum}



        and cause =  #{cause}



        and count =  #{count}



        and time =  #{time}



        and is_del =  #{isDel}



        and customer_code =  #{customerCode}



        and custormer_name =  #{custormerName}



        and authorized_code =  #{authorizedCode}



        and authorized_name =  #{authorizedName}



        and cutting_tool_code =  #{cuttingToolCode}



        and status =  #{status}



        and consume_type =  #{consumeType}

