mapper.insert:
INSERT INTO DJ_OWNER_AKP (

    OWNER_CODE,

    NAME
)
VALUES (

    #{ownerCode},

    #{name}
)

mapper.delete:
UPDATE DJ_OWNER_AKP SET is_del = 1
WHERE
    1=1


        and OWNER_CODE =  #{ownerCode}



        and NAME =  #{name}

mapper.update:
UPDATE DJ_OWNER_AKP SET
NAME = #{name},
OWNER_CODE = #{ownerCode} WHERE 1 = 1 and OWNER_CODE = #{ownerCode}

mapper.select:
SELECT
/*djOwnerAkp begin*/

    DJ_OWNER_AKP.OWNER_CODE as 'DJ_OWNER_AKP.OWNER_CODE',

    DJ_OWNER_AKP.NAME as 'DJ_OWNER_AKP.NAME'
/*djOwnerAkp end*/
FROM DJ_OWNER_AKP as DJ_OWNER_AKP
WHERE
    1 = 1 AND  DJ_OWNER_AKP.is_del = 0
/*DJ_OWNER_AKP begin*/


        and DJ_OWNER_AKP.OWNER_CODE = #{ownerCode}



        and DJ_OWNER_AKP.NAME = #{name}

/*DJ_OWNER_AKP end*/
ORDER BY DJ_OWNER_AKP.OWNER_CODE desc

LIMIT #{startRecord},#{pageSize}

SELECT COUNT(1)
FROM DJ_OWNER_AKP as DJ_OWNER_AKP
WHERE
    1 = 1 AND  DJ_OWNER_AKP.is_del = 0
/*DJ_OWNER_AKP begin*/


    and DJ_OWNER_AKP.OWNER_CODE = #{ownerCode}



    and DJ_OWNER_AKP.NAME = #{name}

/*DJ_OWNER_AKP end*/

SELECT
/*DJ_OWNER_AKP begin*/

DJ_OWNER_AKP.OWNER_CODE as 'DJ_OWNER_AKP.OWNER_CODE',

DJ_OWNER_AKP.NAME as 'DJ_OWNER_AKP.NAME'
/*DJ_OWNER_AKP end*/
FROM DJ_OWNER_AKP as DJ_OWNER_AKP
WHERE
    1 = 1 AND  DJ_OWNER_AKP.is_del = 0
/*DJ_OWNER_AKP begin*/


    and DJ_OWNER_AKP.OWNER_CODE = #{ownerCode}



    and DJ_OWNER_AKP.NAME = #{name}

/*DJ_OWNER_AKP end*/
