synthesis_cutting_tool_bind.is_del as 'synthesis_cutting_tool_bind.is_del',

synthesis_cutting_tool_bind.create_time as 'synthesis_cutting_tool_bind.create_time',

synthesis_cutting_tool_bind.synthesis_cutting_tool_code as 'synthesis_cutting_tool_bind.synthesis_cutting_tool_code',

synthesis_cutting_tool_bind.rfid_container_code as 'synthesis_cutting_tool_bind.rfid_container_code',

synthesis_cutting_tool_bind.synthesis_code as 'synthesis_cutting_tool_bind.synthesis_code',
/*synthesis_cutting_tool_bind end*/
/*product_line_process begin*/

product_line_process.id as 'product_line_process.id',

product_line_process.code as 'product_line_process.code',

product_line_process.assemblyline_code as 'product_line_process.assemblyline_code',

product_line_process.name as 'product_line_process.name',

product_line_process.is_del as 'product_line_process.is_del',
/*product_line_process end*/
/*product_line_assemblyline begin*/

product_line_assemblyline.id as 'product_line_assemblyline.id',

product_line_assemblyline.name as 'product_line_assemblyline.name',

product_line_assemblyline.code as 'product_line_assemblyline.code',

product_line_assemblyline.is_del as 'product_line_assemblyline.is_del'
/*product_line_assemblyline end*/
FROM synthesis_cutting_tool_bindle_records as synthesis_cutting_tool_bindle_records
LEFT JOIN product_line_assemblyline as product_line_assemblyline ON product_line_assemblyline.code = synthesis_cutting_tool_bindle_records.product_line_assemblyline_code
LEFT JOIN product_line_equipment as product_line_equipment ON product_line_equipment.code = synthesis_cutting_tool_bindle_records.product_line_equipment_code
LEFT JOIN product_line_process as product_line_process ON product_line_process.code = synthesis_cutting_tool_bindle_records.product_line_process_code
LEFT JOIN product_line_axle as product_line_axle ON product_line_axle.code = synthesis_cutting_tool_bindle_records.product_line_axle_code
LEFT JOIN product_line_parts as product_line_parts ON product_line_parts.code = synthesis_cutting_tool_bindle_records.product_line_parts_code
LEFT JOIN synthesis_cutting_tool as synthesis_cutting_tool ON synthesis_cutting_tool.code = synthesis_cutting_tool_bindle_records.synthesis_cutting_tool_code
LEFT JOIN synthesis_cutting_tool_bind as synthesis_cutting_tool_bind ON synthesis_cutting_tool_bind.code = synthesis_cutting_tool_bindle_records.synthesis_cutting_tool_bind_code
WHERE
    1 = 1 AND  synthesis_cutting_tool_bindle_records.is_del = 0
/*synthesis_cutting_tool_bindle_records begin*/


    and synthesis_cutting_tool_bindle_records.id = #{id}



    and synthesis_cutting_tool_bindle_records.synthesis_cutting_tool_code = #{synthesisCuttingToolCode}



    and synthesis_cutting_tool_bindle_records.synthesis_code = #{synthesisCode}



    and synthesis_cutting_tool_bindle_records.product_line_equipment_code = #{productLineEquipmentCode}



    and synthesis_cutting_tool_bindle_records.product_line_assemblyline_code = #{productLineAssemblylineCode}



    and synthesis_cutting_tool_bindle_records.product_line_process_code = #{productLineProcessCode}



    and synthesis_cutting_tool_bindle_records.product_line_axle_code = #{productLineAxleCode}



    and synthesis_cutting_tool_bindle_records.product_line_parts_code = #{productLinePartsCode}



    and synthesis_cutting_tool_bindle_records.status = #{status}



    and synthesis_cutting_tool_bindle_records.operator_install = #{operatorInstall}



    and synthesis_cutting_tool_bindle_records.reason = #{reason}



    and synthesis_cutting_tool_bindle_records.rated_number = #{ratedNumber}



    and synthesis_cutting_tool_bindle_records.processing_count = #{processingCount}



    and synthesis_cutting_tool_bindle_records.is_del = #{isDel}



    and synthesis_cutting_tool_bindle_records.operator_uninstall = #{operatorUninstall}



    and synthesis_cutting_tool_bindle_records.operator_install_name = #{operatorInstallName}



    and synthesis_cutting_tool_bindle_records.operator_uninstall_name = #{operatorUninstallName}



    and synthesis_cutting_tool_bindle_records.operator_uninstall_time <= #{operatorUninstallTimeEnd}


    and synthesis_cutting_tool_bindle_records.operator_uninstall_time >= #{operatorUninstallTimeBegin}



    and synthesis_cutting_tool_bindle_records.operator_install_time <= #{operatorInstallTimeEnd}


    and synthesis_cutting_tool_bindle_records.operator_install_time >= #{operatorInstallTimeBegin}



    and synthesis_cutting_tool_bindle_records.synthesis_cutting_tool_bind_code = #{synthesisCuttingToolBindCode}



    and synthesis_cutting_tool_bindle_records.bind_rfid = #{bindRfid}

/*synthesis_cutting_tool_bindle_records end*/
/*product_line_axles begin*/



    and product_line_axles.id = #{productLineAxleVO.id}



    and product_line_axles.code = #{productLineAxleVO.code}



    and product_line_axles.name = #{productLineAxleVO.name}



    and product_line_axles.is_del = #{productLineAxleVO.isDel}


/*product_line_axle end*/
/*synthesis_cutting_tool begin*/



    and synthesis_cutting_tool.id = #{synthesisCuttingToolVO.id}



    and synthesis_cutting_tool.code = #{synthesisCuttingToolVO.code}



    and synthesis_cutting_tool.synthesis_code = #{synthesisCuttingToolVO.synthesisCode}



    and synthesis_cutting_tool.pic_url = #{synthesisCuttingToolVO.picUrl}



    and synthesis_cutting_tool.is_del = #{synthesisCuttingToolVO.isDel}



    and synthesis_cutting_tool.synthesis_cutting_tool_type_id = #{synthesisCuttingToolVO.synthesisCuttingToolTypeId}




/*synthesis_cutting_tool end*/
/*product_line_equipments begin*/



    and product_line_equipments.id = #{productLineEquipmentVO.id}



    and product_line_equipments.rfid_container_code = #{productLineEquipmentVO.rfidContainerCode}



    and product_line_equipments.statues = #{productLineEquipmentVO.statues}



    and product_line_equipments.code = #{productLineEquipmentVO.code}



    and product_line_equipments.name = #{productLineEquipmentVO.name}



    and product_line_equipments.type = #{productLineEquipmentVO.type}



    and product_line_equipments.is_del = #{productLineEquipmentVO.isDel}


/*product_line_equipment end*/
/*product_line_partss begin*/



    and product_line_partss.id = #{productLinePartsVO.id}



    and product_line_partss.code = #{productLinePartsVO.code}



    and product_line_partss.type = #{productLinePartsVO.type}



    and product_line_partss.name = #{productLinePartsVO.name}



    and product_line_partss.is_del = #{productLinePartsVO.isDel}


/*product_line_parts end*/
/*synthesis_cutting_tool_binds begin*/



    and synthesis_cutting_tool_binds.id = #{synthesisCuttingToolBindVO.id}



    and synthesis_cutting_tool_binds.code = #{synthesisCuttingToolBindVO.code}



    and synthesis_cutting_tool_binds.is_del = #{synthesisCuttingToolBindVO.isDel}



    and synthesis_cutting_tool_bind.create_time <=  #{synthesisCuttingToolBindVO.createTimeEnd}


    and synthesis_cutting_tool_bind.create_time >= #{synthesisCuttingToolBindVO.createTimeBegin}



    and synthesis_cutting_tool_binds.synthesis_cutting_tool_code = #{synthesisCuttingToolBindVO.synthesisCuttingToolCode}



    and synthesis_cutting_tool_binds.rfid_container_code = #{synthesisCuttingToolBindVO.rfidContainerCode}



    and synthesis_cutting_tool_binds.synthesis_code = #{synthesisCuttingToolBindVO.synthesisCode}


/*synthesis_cutting_tool_bind end*/
/*product_line_processs begin*/



    and product_line_processs.id = #{productLineProcessVO.id}



    and product_line_processs.code = #{productLineProcessVO.code}



    and product_line_processs.assemblyline_code = #{productLineProcessVO.assemblylineCode}



    and product_line_processs.name = #{productLineProcessVO.name}



    and product_line_processs.is_del = #{productLineProcessVO.isDel}


/*product_line_process end*/
/*product_line_assemblylines begin*/



    and product_line_assemblylines.id = #{productLineAssemblylineVO.id}



    and product_line_assemblylines.name = #{productLineAssemblylineVO.name}



    and product_line_assemblylines.code = #{productLineAssemblylineVO.code}



    and product_line_assemblylines.is_del = #{productLineAssemblylineVO.isDel}


/*product_line_assemblyline end*/
