mapper.delete:
DELETE FROM synthesis_cutting_tool_location_config
WHERE
    1=1


        and id =  #{id}



        and location =  #{location}



        and count =  #{count}



        and is_del =  #{isDel}



        and replace_cutting_tool_code1 =  #{replaceCuttingToolCode1}



        and replace_cutting_tool_code2 =  #{replaceCuttingToolCode2}



        and replace_cutting_tool_code3 =  #{replaceCuttingToolCode3}



        and cutting_tool_code =  #{cuttingToolCode}



        and synthesis_cutting_tool_config_id =  #{synthesisCuttingToolConfigId}



        and type =  #{type}

